import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Temperature from './components/Temperature/temperature';
import Humidity from './components/Humidity/Humidity';
import LightIntensity from './components/LightIntensity/LightIntensity';
import SensorChart from './components/SensorChart/SensorChart';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function App() {
  const [temperature, setTemperature] = useState(0);
  const [humidity, setHumidity] = useState(0);
  const [lightLevel, setLightLevel] = useState(0);
  const [history, setHistory] = useState([]);
  const [timestamps, setTimestamps] = useState([]);
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [historicalData, setHistoricalData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [manualData, setManualData] = useState([]);

  const fetchAllData = async () => {
    try {
      const now = new Date();
      const timeString = `${now.getHours()}:${now.getMinutes()}:${now.getSeconds()}`;
      
      const [tempResponse, humidResponse, lightResponse] = await Promise.all([
        axios.get('http://172.16.22.225:3000/temperature'),
        axios.get('http://172.16.22.225:3000/humidity'),
        axios.get('http://172.16.22.225:3000/lightlevel')
      ]);
      
      setTemperature(tempResponse.data.temperature);
      setHumidity(humidResponse.data.humidity);
      setLightLevel(lightResponse.data.light);
      
      setHistory(prev => {
        const newData = {
          time: timeString,
          temperature: tempResponse.data.temperature,
          humidity: humidResponse.data.humidity,
          light: lightResponse.data.light
        };
        return [...prev.slice(-9), newData];
      });
      
      setTimestamps(prev => [...prev.slice(-9), timeString]);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const fetchHistoricalData = async () => {
    if (!startDate || !endDate) return;
    
    setIsLoading(true);
    try {
      const response = await axios.get('http://172.16.22.225:3000/data/all', {
        params: {
          start: startDate.toISOString(),
          end: endDate.toISOString()
        }
      });
      setHistoricalData(response.data);
    } catch (error) {
      console.error('Error fetching historical data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddToChart = (value, type) => {
    const now = new Date();
    const timeString = `${now.getHours()}:${now.getMinutes()}:${now.getSeconds()}`;
    
    const newDataPoint = {
      time: timeString,
      temperature: type === 'temperature' ? value : null,
      humidity: type === 'humidity' ? value : null,
      light: type === 'light' ? value : null
    };
    
    setManualData(prev => [...prev, newDataPoint]);
    setTimestamps(prev => [...prev, timeString]);
  };

  useEffect(() => {
    fetchAllData();
    const interval = setInterval(fetchAllData, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <div className="sensors-container">
        <Temperature 
          temperature={temperature} 
          onAddToChart={() => handleAddToChart(temperature, 'temperature')}
        />
        <Humidity 
          humidity={humidity}
          onAddToChart={() => handleAddToChart(humidity, 'humidity')}
        />
        <LightIntensity 
          lightLevel={lightLevel}
          onAddToChart={() => handleAddToChart(lightLevel, 'light')}
        />
      </div>
  
      <SensorChart 
        timestamps={timestamps}
        history={[...history, ...manualData]}
        historicalData={historicalData}
        startDate={startDate}
        endDate={endDate}
      />
    </div>
  );
}

export default App;
