import React from 'react';
import './WaterPresence.css';

const Temperature = ({ waterLevel }) => {
  // –¶–≤–µ—Ç–æ–≤–∞—è —Å—Ö–µ–º–∞
  const colors = {
    normal: '#4A90E2',       // –ì–æ–ª—É–±–æ–π (–Ω–æ—Ä–º–∞–ª—å–Ω—ã–π —É—Ä–æ–≤–µ–Ω—å)
    warning: '#F0A830',      // –ñ–µ–ª—Ç—ã–π (–Ω–∏–∑–∫–∏–π —É—Ä–æ–≤–µ–Ω—å)
    danger: '#E05038',       // –ö—Ä–∞—Å–Ω—ã–π (–∫—Ä–∏—Ç–∏—á–µ—Å–∫–∏–π —É—Ä–æ–≤–µ–Ω—å)
    textDark: '#1E3B23',     // –¢–µ–º–Ω–æ-–∑–µ–ª–µ–Ω—ã–π —Ç–µ–∫—Å—Ç
    textLight: '#F5F7E6',    // –°–≤–µ—Ç–ª—ã–π —Ç–µ–∫—Å—Ç
    water: '#5D9CEC',        // –¶–≤–µ—Ç –≤–æ–¥—ã
    noWater: '#A0AEC0'       // –¶–≤–µ—Ç –æ—Ç—Å—É—Ç—Å—Ç–≤–∏—è –≤–æ–¥—ã
  };

  // –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Å—Ç–∞—Ç—É—Å–∞ –≤–æ–¥—ã
  const getWaterStatus = level => {
    if (level === null) return 'nodata';
    if (level === 0) return 'nowater';
    if (level < 30) return 'danger';
    if (level < 70) return 'warning';
    return 'normal';
  };

  const status = getWaterStatus(waterLevel);
  const statusMessages = {
    nodata: '–î–∞–Ω–Ω—ã–µ –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç',
    nowater: '–í–æ–¥–∞ –Ω–µ –æ–±–Ω–∞—Ä—É–∂–µ–Ω–∞!',
    danger: '–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏ –Ω–∏–∑–∫–∏–π —É—Ä–æ–≤–µ–Ω—å!',
    warning: '–ù–∏–∑–∫–∏–π —É—Ä–æ–≤–µ–Ω—å –≤–æ–¥—ã',
    normal: '–ù–æ—Ä–º–∞–ª—å–Ω—ã–π —É—Ä–æ–≤–µ–Ω—å'
  };

  const statusIcons = {
    nodata: '‚ùì',
    nowater: 'üö±',
    danger: '‚ö†Ô∏è',
    warning: 'üîç',
    normal: '‚úÖ'
  };

  return (
    <div className="water-card">
      <div className="water-header">
        <h2 className="water-title">
          <span className="icon">üíß</span> –£—Ä–æ–≤–µ–Ω—å –≤–æ–¥—ã
        </h2>
        <div className="system-icons">
          <span className="icon">üåø</span>
          <span className="icon">üêü</span>
        </div>
      </div>

      <div className="water-display">
        {waterLevel !== null ? (
          <>
            <div className="water-visualization">
              <div 
                className="water-level"
                style={{
                  height: `${waterLevel}%`,
                  background: `linear-gradient(to top, ${colors.water}, #7AB3EF)`
                }}
              />
              <div className="water-container" />
            </div>
            <div className="water-value">
              {waterLevel}% <span className="water-status-icon">{statusIcons[status]}</span>
            </div>
          </>
        ) : (
          <div className="no-data">
            <span className="icon">‚ùì</span> –ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö
          </div>
        )}
      </div>

      <div className="water-footer">
        <p className={`status-message status-${status}`}>
          {statusMessages[status]}
        </p>
      </div>
    </div>
  );
};

export default Temperature;
