import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import './SensorChart.css';
import Button from '../Button/Button';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const SensorChart = ({ timestamps, history, historicalData, startDate, endDate }) => {
  const [showChart, setShowChart] = useState(true);

  const toggleChartVisibility = () => {
    setShowChart(!showChart);
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return `${date.getHours()}:${date.getMinutes()}:${date.getSeconds()}`;
  };

  const useHistoricalData = historicalData && startDate && endDate;
  
  const chartData = {
    labels: useHistoricalData 
      ? historicalData.temperature.map(item => formatDate(item.timestamp))
      : timestamps,
    datasets: [
      {
        label: 'Temperature (°C)',
        data: useHistoricalData 
          ? historicalData.temperature.map(item => item.value)
          : history.map(item => item.temperature),
        borderColor: 'rgb(255, 99, 132)',
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
        tension: 0.1,
        borderWidth: 2,
        pointRadius: 3
      },
      {
        label: 'Humidity (%)',
        data: useHistoricalData 
          ? historicalData.humidity.map(item => item.value)
          : history.map(item => item.humidity),
        borderColor: 'rgb(54, 162, 235)',
        backgroundColor: 'rgba(54, 162, 235, 0.5)',
        tension: 0.1,
        borderWidth: 2,
        pointRadius: 3
      },
      {
        label: 'Light Level',
        data: useHistoricalData 
          ? historicalData.light.map(item => item.value)
          : history.map(item => item.light),
        borderColor: 'rgb(255, 206, 86)',
        backgroundColor: 'rgba(255, 206, 86, 0.5)',
        tension: 0.1,
        borderWidth: 2,
        pointRadius: 3
      }
    ]
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          font: {
            size: 12
          }
        }
      },
      title: {
        display: true,
        text: useHistoricalData 
          ? `График показателей с ${startDate.toLocaleString()} по ${endDate.toLocaleString()}`
          : 'График текущих показателей',
        font: {
          size: 16
        }
      },
    },
    scales: {
      y: {
        beginAtZero: false
      }
    }
  };

  return (
    <div className="sensor-chart-container">
      <div className="chart-controls">
        <Button 
          onClick={toggleChartVisibility}
          label={showChart ? 'Скрыть график' : 'Показать график'}
        />
      </div>
      
      {showChart ? (
        <div className="sensor-chart">
          <Line options={options} data={chartData} />
        </div>
      ) : (
        <div className="chart-hidden-message">
          График скрыт. Нажмите "Показать график" для отображения данных.
        </div>
      )}
    </div>
  );
};

export default SensorChart;
