import { useNavigate } from 'react-router-dom';

function Navbar({ setToken, token }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    setToken(null);
    navigate('/login');
  };

  return (
    <nav style={{
      backgroundColor: '#1a4314',
      padding: '20px 40px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        maxWidth: '1200px',
        margin: '0 auto'
      }}>
        {/* Левый блок - название и навигация */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '40px' }}>
          <span style={{
            color: '#f5d742',
            fontSize: '24px',
            fontWeight: 'bold',
            cursor: 'pointer'
          }} onClick={() => navigate('/')}>
            Fish & Greens
          </span>

          <div style={{ display: 'flex', gap: '25px' }}>
            <NavButton onClick={() => navigate('/')}>Главная</NavButton>
            <NavButton onClick={() => navigate('/plants')}>Наши растения</NavButton>
            {token && <NavButton onClick={() => navigate('/dashboard')}>Мое устройство</NavButton>}
          </div>
        </div>

        {/* Правый блок - вход/выход */}
        {token ? (
          <NavButton 
            onClick={handleLogout}
            style={{
              backgroundColor: 'rgba(245, 215, 66, 0.2)',
              border: '2px solid #f5d742'
            }}
          >
            Выход
          </NavButton>
        ) : (
          <NavButton 
            onClick={() => navigate('/login')}
            style={{
              backgroundColor: 'rgba(245, 215, 66, 0.2)',
              border: '2px solid #f5d742'
            }}
          >
            Вход
          </NavButton>
        )}
      </div>
    </nav>
  );
}

// Компонент кнопки навигации
function NavButton({ children, onClick, style = {} }) {
  return (
    <button
      onClick={onClick}
      style={{
        color: '#f5d742',
        fontSize: '16px',
        fontWeight: '600',
        background: 'transparent',
        border: 'none',
        cursor: 'pointer',
        padding: '8px 12px',
        borderRadius: '6px',
        transition: 'all 0.3s ease',
        ...style
      }}
      onMouseEnter={(e) => {
        e.target.style.backgroundColor = style.backgroundColor 
          ? 'rgba(245, 215, 66, 0.3)' 
          : 'rgba(245, 215, 66, 0.1)';
        e.target.style.transform = 'translateY(-2px)';
      }}
      onMouseLeave={(e) => {
        e.target.style.backgroundColor = style.backgroundColor || 'transparent';
        e.target.style.transform = 'translateY(0)';
      }}
    >
      {children}
    </button>
  );
}

export default Navbar;
