import { useState, useEffect } from 'react';
import axios from 'axios';

function Dashboard() {
  const [data, setData] = useState({ temperature: 0, humidity: 0, light: 0 });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [tempRes, humRes, lightRes] = await Promise.all([
          axios.get('http://localhost:3000/temperature?device_id=dev1'),
          axios.get('http://localhost:3000/humidity?device_id=dev1'),
          axios.get('http://localhost:3000/lightlevel?device_id=dev1'),
        ]);
        setData({
          temperature: tempRes.data.temperature,
          humidity: humRes.data.humidity,
          light: lightRes.data.light,
        });
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };
    fetchData();
    const interval = setInterval(fetchData, 10000); // Обновление каждые 10 секунд
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-4xl text-black text-center mb-6">Показатели вашего устройства</h1>
      <div className="grid grid-cols-3 gap-6 mb-6">
        <div className="dark-green p-4 rounded-lg text-white text-center">
          <p>Температура</p>
          <p>{data.temperature} °C</p>
        </div>
        <div className="dark-green p-4 rounded-lg text-white text-center">
          <p>Свет</p>
          <p>{data.light} lux</p>
        </div>
        <div className="dark-green p-4 rounded-lg text-white text-center">
          <p>Влажность</p>
          <p>{data.humidity} %</p>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
