import { useState, useEffect } from 'react';
import axios from 'axios';

function Dashboard() {
  const [data, setData] = useState({ temperature: 0, humidity: 0, light: 0 });
  const [deviceId, setDeviceId] = useState('');

  useEffect(() => {
    // Получаем device_id из localStorage (или другого места, где он хранится после авторизации)
    const userDeviceId = localStorage.getItem('device_id'); // предполагается, что device_id сохранен в localStorage
    setDeviceId(userDeviceId);

    const fetchData = async () => {
      if (!userDeviceId) return; // если device_id нет, не делаем запрос

      try {
        const [tempRes, humRes, lightRes] = await Promise.all([
          axios.get(`http://localhost:3000/data/temperature?device_id=${userDeviceId}`),
          axios.get(`http://localhost:3000/data/humidity?device_id=${userDeviceId}`),
          axios.get(`http://localhost:3000/data/light?device_id=${userDeviceId}`),
        ]);
        setData({
          temperature: tempRes.data[0].value, // предполагается, что данные возвращаются в виде массива
          humidity: humRes.data[0].value,
          light: lightRes.data[0].value,
        });
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 10000); // Обновление каждые 10 секунд
    return () => clearInterval(interval);
  }, [deviceId]); // зависимость от deviceId, чтобы запрос обновлялся при изменении

  return (
    <div className="p-6">
      <h1 className="text-4xl text-black text-center mb-6">Показатели вашего устройства</h1>
      <div className="grid grid-cols-3 gap-6 mb-6">
        <div className="dark-green p-4 rounded-lg text-white text-center">
          <p>Температура</p>
          <p>{data.temperature} °C</p>
        </div>
        <div className="dark-green p-4 rounded-lg text-white text-center">
          <p>Свет</p>
          <p>{data.light} lux</p>
        </div>
        <div className="dark-green p-4 rounded-lg text-white text-center">
          <p>Влажность</p>
          <p>{data.humidity} %</p>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
