import React, { useState, useEffect } from 'react';
import './Temperature.css';
import Button from '../Button/Button';

const Temperature = ({ temperature }) => {
  const [displayData, setDisplayData] = useState({
    value: true,
    scale: true,
    status: true
  });

  const colors = {
    water: '#4A89DC',
    plant: '#8CC152',
    fish: '#3BAFDA',
    soil: '#A37F6B',
    danger: '#E9573F',
    textDark: '#2C3E50',
    textLight: '#F5F7FA'
  };

  const getTempColor = (temp) => {
    if (temp === null) return colors.textDark;
    if (temp < 14) return colors.water;
    if (temp < 26) return colors.plant;
    if (temp < 30) return colors.fish;
    return colors.danger;
  };

  const toggleData = () => {
    if (displayData.value && displayData.scale && displayData.status) {
      // –ï—Å–ª–∏ –≤—Å–µ –ø–æ–∫–∞–∑–∞–Ω–æ - —Å–∫—Ä—ã–≤–∞–µ–º –≤—Å–µ
      setDisplayData({ value: false, scale: false, status: false });
    } else {
      // –ò–Ω–∞—á–µ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –≤—Å–µ
      setDisplayData({ value: true, scale: true, status: true });
    }
  };

  return (
    <div className="temperature-card">
      <div className="temperature-header">
        <h2 className="temperature-title">
          <span className="icon">üå±</span> –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞
        </h2>
        <div className="ecosystem-icons">
          <span className="icon">üíß</span>
          <span className="icon">üêü</span>
        </div>
      </div>

      <div className="temperature-display">
        {displayData.value && (
          <div className="temperature-value" style={{ color: getTempColor(temperature) }}>
            {temperature !== null ? temperature.toFixed(1) : '--'}
            <span className="temperature-unit">¬∞C</span>
          </div>
        )}

        {displayData.scale && (
          <div className="temperature-scale">
            <span style={{ color: colors.water }}>10¬∞</span>
            <div className="scale-bar">
              <div 
                className="scale-progress" 
                style={{
                  width: temperature ? `${Math.min(100, Math.max(0, (temperature - 10) / 40 * 100))}%` : '0%',
                  background: getTempColor(temperature)
                }}
              />
            </div>
            <span style={{ color: colors.danger }}>50¬∞</span>
          </div>
        )}
      </div>

      <div className="temperature-footer">
        {displayData.status && temperature !== null && (
          <p className="status-message">
            {temperature < 14 ? '‚ùÑÔ∏è –ü—Ä–æ—Ö–ª–∞–¥–Ω–æ' :
             temperature < 26 ? 'üåø –ù–æ—Ä–º–∞' :
             temperature < 30 ? '‚ö†Ô∏è –¢–µ–ø–ª–æ' : 'üî• –û–ø–∞—Å–Ω–æ'}
          </p>
        )}
        <Button 
          onClick={toggleData}
          label={displayData.value ? '–°–∫—Ä—ã—Ç—å –¥–∞–Ω–Ω—ã–µ' : '–ü–æ–∫–∞–∑–∞—Ç—å –¥–∞–Ω–Ω—ã–µ'}
        />
      </div>
    </div>
  );
};

export default Temperature;
