import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import Temperature from './Temperature/temperature';
import Humidity from './Humidity/Humidity';
import LightIntensity from './LightIntensity/LightIntensity';
import SensorChart from './SensorChart/SensorChart';

import './App.css';

const LoadingSpinner = () => (
  <div className="loading-spinner">
    <div className="spinner"></div>
    <p>Загрузка...</p>
  </div>
);

const ErrorMessage = ({ message, onRetry }) => (
  <div className="error-message">
    <p>{message}</p>
    <button className="retry-button" onClick={onRetry}>
      Повторить попытку
    </button>
  </div>
);

const API_CONFIG = {
  baseURL: 'http://localhost:3000',
  endpoints: {
    device: '/user/device',
    currentTemp: '/temperature',
    currentHumid: '/humidity',
    currentLight: '/lightlevel',
    historicalTemp: '/data/temperature',
    historicalHumid: '/data/humidity',
    historicalLight: '/data/light'
  }
};

function Dashboard() {
  const [sensorData, setSensorData] = useState({
    temperature: null,
    humidity: null,
    light: null
  });
  const [deviceId, setDeviceId] = useState(null);
  const [historicalData, setHistoricalData] = useState(null);
  const [timeRange, setTimeRange] = useState({
    startDate: new Date(Date.now() - 24 * 60 * 60 * 1000),
    endDate: new Date()
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchDeviceId = useCallback(async () => {
    let isMounted = true;
    const abortController = new AbortController();

    try {
      const response = await axios.get(API_CONFIG.endpoints.device, {
        baseURL: API_CONFIG.baseURL,
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        },
        signal: abortController.signal
      });

      if (isMounted) {
        setDeviceId(response.data.device_id);
        console.log('Получен device_id:', response.data.device_id);
      }
    } catch (err) {
      if (isMounted) {
        if (axios.isCancel(err)) {
          console.log('Запрос отменен:', err.message);
        } else {
          setError('Не удалось получить данные устройства');
          console.error('Ошибка получения device ID:', err);
        }
      }
    }

    return () => {
      isMounted = false;
      abortController.abort();
    };
  }, []);

  const fetchCurrentData = useCallback(async () => {
    if (!deviceId) {
      console.log('device_id отсутствует, пропускаем запрос данных');
      return;
    }

    try {
      console.log('Запрос текущих данных для device_id:', deviceId);
      const [tempRes, humRes, lightRes] = await Promise.all([
        axios.get(API_CONFIG.endpoints.currentTemp, {
          baseURL: API_CONFIG.baseURL,
          params: { device_id: deviceId }
        }),
        axios.get(API_CONFIG.endpoints.currentHumid, {
          baseURL: API_CONFIG.baseURL,
          params: { device_id: deviceId }
        }),
        axios.get(API_CONFIG.endpoints.currentLight, {
          baseURL: API_CONFIG.baseURL,
          params: { device_id: deviceId }
        })
      ]);
      
      setSensorData({
        temperature: tempRes.data?.temperature ?? null,
        humidity: humRes.data?.humidity ?? null,
        light: lightRes.data?.light ?? null
      });
    } catch (err) {
      console.error('Ошибка получения данных датчиков для device_id:', deviceId, err);
    }
  }, [deviceId]);

  const fetchHistoricalData = useCallback(async (start, end) => {
    if (!deviceId) {
      console.log('device_id отсутствует, пропускаем запрос исторических данных');
      return;
    }

    try {
      setLoading(true);
      console.log('Запрос исторических данных для device_id:', deviceId);
      const [tempData, humData, lightData] = await Promise.all([
        axios.get(API_CONFIG.endpoints.historicalTemp, {
          baseURL: API_CONFIG.baseURL,
          params: {
            device_id: deviceId,
            start: start.toISOString(),
            end: end.toISOString()
          }
        }),
        axios.get(API_CONFIG.endpoints.historicalHumid, {
          baseURL: API_CONFIG.baseURL,
          params: {
            device_id: deviceId,
            start: start.toISOString(),
            end: end.toISOString()
          }
        }),
        axios.get(API_CONFIG.endpoints.historicalLight, {
          baseURL: API_CONFIG.baseURL,
          params: {
            device_id: deviceId,
            start: start.toISOString(),
            end: end.toISOString()
          }
        })
      ]);
      
      setHistoricalData({
        temperature: tempData.data || null,
        humidity: humData.data || null,
        light: lightData.data || null
      });
    } catch (err) {
      setError(`Ошибка загрузки исторических данных для устройства ${deviceId}`);
      console.error(`Ошибка получения исторических данных для device_id ${deviceId}:`, err);
    } finally {
      setLoading(false);
    }
  }, [deviceId]);

  const handleTimeRangeChange = useCallback((newStartDate, newEndDate) => {
    setTimeRange({
      startDate: newStartDate,
      endDate: newEndDate
    });
    fetchHistoricalData(newStartDate, newEndDate);
  }, [fetchHistoricalData]);

  useEffect(() => {
    const cancelToken = axios.CancelToken.source();
    fetchDeviceId();

    return () => {
      cancelToken.cancel('Компонент размонтирован');
    };
  }, [fetchDeviceId]);

  useEffect(() => {
    fetchCurrentData();
    const intervalId = setInterval(fetchCurrentData, 10000);
    
    return () => {
      clearInterval(intervalId);
    };
  }, [fetchCurrentData]);

  useEffect(() => {
    fetchHistoricalData(timeRange.startDate, timeRange.endDate);
  }, [fetchHistoricalData, timeRange]);

  if (error) {
    return (
      <ErrorMessage 
        message={error} 
        onRetry={() => {
          setError(null);
          fetchDeviceId();
        }} 
      />
    );
  }

  if (!deviceId || loading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">
        Показатели устройства {deviceId}
      </h1>
      
      <div className="sensors-grid">
        <Temperature temperature={sensorData.temperature}/>
        <Humidity humidity={sensorData.humidity}/>
        <LightIntensity lightLevel={sensorData.light}/>
      </div>
      
      {historicalData && (
        <SensorChart 
          historicalData={historicalData}
          startDate={timeRange.startDate}
          endDate={timeRange.endDate}
          onTimeRangeChange={handleTimeRangeChange}
          deviceId={deviceId}
        />
      )}
    </div>
  );
}

export default Dashboard;
