  "–ê–∫–≤–∞–ø–æ–Ω–∏–∫–∞ –∏–º–∏—Ç–∏—Ä—É–µ—Ç –µ—Å—Ç–µ—Å—Ç–≤–µ–Ω–Ω—ã–µ –≤–æ–¥–Ω—ã–µ —ç–∫–æ—Å–∏—Å—Ç–µ–º—ã!",
  "–í–æ–¥–∞ –≤ —Å–∏—Å—Ç–µ–º–µ —Ü–∏—Ä–∫—É–ª–∏—Ä—É–µ—Ç –ø–æ—Å—Ç–æ—è–Ω–Ω–æ, —Å–æ–∑–¥–∞–≤–∞—è –∏–¥–µ–∞–ª—å–Ω—ã–µ —É—Å–ª–æ–≤–∏—è!",
  "–ù–µ—Ç –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ—Å—Ç–∏ –≤ —Ö–∏–º–∏—á–µ—Å–∫–∏—Ö —É–¥–æ–±—Ä–µ–Ω–∏—è—Ö - –≤—Å–µ –Ω–∞—Ç—É—Ä–∞–ª—å–Ω–æ!"
];

function Dashboard() {
  const [sensorData, setSensorData] = useState({
    temperature: null,
    humidity: null,
    light: null
  });
  const [deviceId, setDeviceId] = useState(null);
  const [historicalData, setHistoricalData] = useState(null);
  const [timeRange, setTimeRange] = useState({
    startDate: new Date(Date.now() - 24 * 60 * 60 * 1000),
    endDate: new Date()
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentFact, setCurrentFact] = useState('');
  const [fishImage, setFishImage] = useState('üêü');
  const [plantImage, setPlantImage] = useState('üå±');

  const fetchDeviceId = useCallback(async () => {
    let isMounted = true;
    const abortController = new AbortController();

    try {
      const response = await axios.get(API_CONFIG.endpoints.device, {
        baseURL: API_CONFIG.baseURL,
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        },
        signal: abortController.signal
      });

      if (isMounted) {
        setDeviceId(response.data.device_id);
      }
    } catch (err) {
      if (isMounted && !axios.isCancel(err)) {
        setError('–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –¥–∞–Ω–Ω—ã–µ —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞');
      }
    }

    return () => {
      isMounted = false;
      abortController.abort();
    };
  }, []);

  const fetchCurrentData = useCallback(async () => {
    if (!deviceId) return;

    try {
      const [tempRes, humRes, lightRes] = await Promise.all([
        axios.get(API_CONFIG.endpoints.currentTemp, {
          baseURL: API_CONFIG.baseURL,
          params: { device_id: deviceId }
        }),
        axios.get(API_CONFIG.endpoints.currentHumid, {
          baseURL: API_CONFIG.baseURL,
          params: { device_id: deviceId }
        }),
        axios.get(API_CONFIG.endpoints.currentLight, {
          baseURL: API_CONFIG.baseURL,
          params: { device_id: deviceId }
        })
      ]);
      
      setSensorData({
        temperature: tempRes.data?.temperature ?? null,
        humidity: humRes.data?.humidity ?? null,
        light: lightRes.data?.light ?? null
      });
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø–æ–ª—É—á–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –¥–∞—Ç—á–∏–∫–æ–≤:', err);
    }
  }, [deviceId]);

  const fetchHistoricalData = useCallback(async (start, end) => {
    if (!deviceId) return;

    try {
      setLoading(true);
      const [tempData, humData, lightData] = await Promise.all([
        axios.get(API_CONFIG.endpoints.historicalTemp, {
          baseURL: API_CONFIG.baseURL,
          params: {
            device_id: deviceId,
            start: start.toISOString(),
            end: end.toISOString()
          }
        }),
        axios.get(API_CONFIG.endpoints.historicalHumid, {
          baseURL: API_CONFIG.baseURL,
          params: {
            device_id: deviceId,
            start: start.toISOString(),
            end: end.toISOString()
          }
        }),
        axios.get(API_CONFIG.endpoints.historicalLight, {
          baseURL: API_CONFIG.baseURL,
          params: {
            device_id: deviceId,
            start: start.toISOString(),
            end: end.toISOString()
          }
        })
      ]);
      
      setHistoricalData({
        temperature: tempData.data || null,
        humidity: humData.data || null,
        light: lightData.data || null
      });
    } catch (err) {
      setError(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∏—Å—Ç–æ—Ä–∏—á–µ—Å–∫–∏—Ö –¥–∞–Ω–Ω—ã—Ö`);
    } finally {
      setLoading(false);
    }
  }, [deviceId]);

  const handleTimeRangeChange = useCallback((newStartDate, newEndDate) => {
    setTimeRange({
      startDate: newStartDate,
      endDate: newEndDate
    });
    fetchHistoricalData(newStartDate, newEndDate);
  }, [fetchHistoricalData]);

  useEffect(() => {
    // –£—Å—Ç–∞–Ω–æ–≤–∫–∞ —Å–ª—É—á–∞–π–Ω–æ–≥–æ —Ñ–∞–∫—Ç–∞ –∏ –∏–∫–æ–Ω–æ–∫
    setCurrentFact(AQUAPONICS_FACTS[Math.floor(Math.random() * AQUAPONICS_FACTS.length)]);
    setFishImage(['üêü', 'üê†', 'ü¶ê', 'üê°'][Math.floor(Math.random() * 4)]);
    setPlantImage(['üå±', 'üåø', 'üçÄ', '‚òòÔ∏è'][Math.floor(Math.random() * 4)]);

    const factInterval = setInterval(() => {
      setCurrentFact(AQUAPONICS_FACTS[Math.floor(Math.random() * AQUAPONICS_FACTS.length)]);
      setFishImage(['üêü', 'üê†', 'ü¶ê', 'üê°'][Math.floor(Math.random() * 4)]);
      setPlantImage(['üå±', 'üåø', 'üçÄ', '‚òòÔ∏è'][Math.floor(Math.random() * 4)]);
    }, 30000);

    return () => clearInterval(factInterval);
  }, []);

  useEffect(() => {
    fetchDeviceId();
  }, [fetchDeviceId]);

  useEffect(() => {
    fetchCurrentData();
    const intervalId = setInterval(fetchCurrentData, 10000);
    return () => clearInterval(intervalId);
  }, [fetchCurrentData]);

  useEffect(() => {
    fetchHistoricalData(timeRange.startDate, timeRange.endDate);
  }, [fetchHistoricalData, timeRange]);

  if (error) {
    return (
      <div className="dashboard-root">
        <div className="dashboard-container">
          <ErrorMessage 
            message={error} 
            onRetry={() => {
              setError(null);
              fetchDeviceId();
            }} 
          />
        </div>
      </div>
    );
  }

  if (!deviceId || loading) {
    return (
      <div className="dashboard-root">
        <div className="dashboard-container">
          <LoadingSpinner />
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-root">
      <div className="dashboard-container">
        <h1 className="dashboard-title">–ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –∞–∫–≤–∞–ø–æ–Ω–∏–∫–æ–π</h1>
        
        {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏–æ–Ω–Ω–∞—è –ø–ª–∞—à–∫–∞ */}
        <div className="aquaponics-card">
          <div className="device-info-section">
            <h3>–í–∞—à–∞ —Å–∏—Å—Ç–µ–º–∞ #{deviceId}</h3>
            <div className="status-indicator">
              <span className="status-dot"></span>
              <span>–ê–∫—Ç–∏–≤–Ω–∞</span>
            </div>
          </div>
          
          <div className="fact-section">
            <div className="icons-row">
              <span className="icon">{fishImage}</span>
              <span className="icon">{plantImage}</span>
            </div>
            <div className="fact-text">
              <h4>–ó–Ω–∞–µ—Ç–µ –ª–∏ –≤—ã?</h4>
              <p>{currentFact}</p>
            </div>
          </div>
        </div>
        
        <div className="sensors-grid">
          <Temperature temperature={sensorData.temperature}/>
          <Humidity humidity={sensorData.humidity}/>
          <LightIntensity lightLevel={sensorData.light}/>
        </div>
        
        {historicalData && (
          <SensorChart 
            historicalData={historicalData}
            startDate={timeRange.startDate}
            endDate={timeRange.endDate}
            onTimeRangeChange={handleTimeRangeChange}
            deviceId={deviceId}
          />
        )}
      </div>
    </div>
  );
}

export default Dashboard;
