import { useNavigate } from 'react-router-dom';

function Navbar({ setToken }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    setToken(null);
    navigate('/login');
  };

  return (
    <nav style={{
      backgroundColor: '#1a4314',
      padding: '20px 40px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
      position: 'sticky',
      top: 0,
      zIndex: 1000
    }}>
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        maxWidth: '1200px',
        margin: '0 auto'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '40px' }}>
          <span style={{
            color: 'white',
            fontSize: '24px',
            fontWeight: 'bold',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: '10px'
          }} onClick={() => navigate('/dashboard')}>
            <img 
              src="/logo.svg" 
              alt="Logo" 
              style={{ height: '40px' }}
            />
            Fish & Greens
          </span>

          <div style={{ display: 'flex', gap: '30px' }}>
            <button
              style={{
                color: '#f5d742',
                fontSize: '18px',
                fontWeight: '600',
                background: 'transparent',
                border: 'none',
                cursor: 'pointer',
                padding: '8px 12px',
                borderRadius: '6px',
                transition: 'all 0.3s ease',
              }}
              onMouseEnter={(e) => e.target.style.backgroundColor = 'rgba(245, 215, 66, 0.1)'}
              onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
              onClick={() => navigate('/dashboard')}
            >
              Главная
            </button>
            <button
              style={{
                color: '#f5d742',
                fontSize: '18px',
                fontWeight: '600',
                background: 'transparent',
                border: 'none',
                cursor: 'pointer',
                padding: '8px 12px',
                borderRadius: '6px',
                transition: 'all 0.3s ease',
              }}
              onMouseEnter={(e) => e.target.style.backgroundColor = 'rgba(245, 215, 66, 0.1)'}
              onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
              onClick={() => navigate('/plants')}
            >
              Наши растения
            </button>
          </div>
        </div>

        <button
          onClick={handleLogout}
          style={{
            color: '#f5d742',
            fontSize: '18px',
            fontWeight: '600',
            background: 'rgba(245, 215, 66, 0.2)',
            border: '2px solid #f5d742',
            cursor: 'pointer',
            padding: '10px 20px',
            borderRadius: '8px',
            transition: 'all 0.3s ease',
          }}
          onMouseEnter={(e) => {
            e.target.style.backgroundColor = 'rgba(245, 215, 66, 0.3)';
            e.target.style.transform = 'translateY(-2px)';
          }}
          onMouseLeave={(e) => {
            e.target.style.backgroundColor = 'rgba(245, 215, 66, 0.2)';
            e.target.style.transform = 'translateY(0)';
          }}
        >
          Выход
        </button>
      </div>
    </nav>
  );
}

export default Navbar;
