import React from 'react';
import './Humidity.css';

const Humidity = ({ humidity }) => {
  const colors = {
    dark: '#3A5C40',        // –¢–µ–º–Ω–æ-—Ö–≤–æ–π–Ω—ã–π (–Ω–µ–¥–æ—Å—Ç–∞—Ç–æ–∫ —Å–≤–µ—Ç–∞)
    low: '#5D8C5A',         // –ú–æ–ª–æ–¥–∞—è –ª–∏—Å—Ç–≤–∞
    ideal: '#7CB45D',       // –°–æ—á–Ω–∞—è –∑–µ–ª–µ–Ω—å
    high: '#F0A830',        // –°–æ–ª–Ω–µ—á–Ω—ã–π –æ—Ä–µ–æ–ª
    danger: '#E05038',      // –û—Å–µ–Ω–Ω–∏–π –∫—Ä–∞—Å–Ω—ã–π
    textDark: '#1E3B23',
    textLight: '#F5F7E6',
    moss: '#8AAE92',
    fern: '#6B8C42',
    dry: '#E05038',         // –î–æ–±–∞–≤–ª–µ–Ω –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—â–∏–π —Ü–≤–µ—Ç
    saturated: '#3A5C40'    // –î–æ–±–∞–≤–ª–µ–Ω –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—â–∏–π —Ü–≤–µ—Ç
  };

  const getHumidityColor = (hum) => {
    if (hum === null) return colors.textDark;
    if (hum < 30) return colors.dry;
    if (hum < 80) return colors.ideal;
    return colors.saturated;
  };

  const getStatusMessage = (hum) => {
    if (hum === null) return '–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö';
    if (hum < 30) return 'üèúÔ∏è –°—É—Ö–æ';
    if (hum < 60) return 'üå± –ù–æ—Ä–º–∞';
    if (hum < 80) return 'üíß –í–ª–∞–∂–Ω–æ';
    return 'üåä –û—á–µ–Ω—å –≤–ª–∞–∂–Ω–æ';
  };

  return (
    <div className="humidity-card">
      <div className="humidity-header">
        <h2 className="humidity-title">
          <span className="icon">üíß</span> –í–ª–∞–∂–Ω–æ—Å—Ç—å
        </h2>
        <div className="weather-icons">
          <span className="icon">‚òÄÔ∏è</span>
          <span className="icon">üåßÔ∏è</span>
        </div>
      </div>

      <div className="humidity-display">
        <div className="humidity-value" style={{ color: getHumidityColor(humidity) }}>
          {humidity !== null ? humidity.toFixed(0) : '--'}
          <span className="humidity-unit">%</span>
        </div>
        <div className="humidity-scale">
          <span style={{ color: colors.dry }}>0%</span>
          <div className="scale-bar">
            <div 
              className="scale-progress" 
              style={{
                width: humidity ? `${humidity}%` : '0%',
                backgroundColor: getHumidityColor(humidity)
              }}
            />
          </div>
          <span style={{ color: colors.saturated }}>100%</span>
        </div>
      </div>

      <div className="humidity-footer">
        <p className="status-message">
          {getStatusMessage(humidity)}
        </p>
      </div>
    </div>
  );
};

export default Humidity;
