import React, { useEffect, useRef } from 'react';
import './Temperature.css';

const Temperature  = ({ waterLevel }) => {
  const waterRef = useRef(null);
  
  const colors = {
    normal: '#4A90E2',
    warning: '#F0A830',
    danger: '#E05038',
    textDark: '#1E3B23',
    textLight: '#F5F7E6',
    water: '#5D9CEC',
    noWater: '#A0AEC0',
    bubble: 'rgba(255, 255, 255, 0.4)'
  };

  const getWaterStatus = level => {
    if (level === null) return 'nodata';
    if (level === 0) return 'nowater';
    if (level < 30) return 'danger';
    if (level < 70) return 'warning';
    return 'normal';
  };

  const status = getWaterStatus(waterLevel);
  
  const statusConfig = {
    nodata: { icon: '‚ùì', message: '–î–∞–Ω–Ω—ã–µ –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç', color: '#EDF2F7', text: '#4A5568' },
    nowater: { 
      icon: 'üö±', 
      message: '–í–æ–¥–∞ –Ω–µ –æ–±–Ω–∞—Ä—É–∂–µ–Ω–∞!', 
      color: '#FFF5F5', 
      text: '#C53030',
      animation: 'pulse 1.5s infinite'
    },
    danger: { 
      icon: '‚ö†Ô∏è', 
      message: '–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏ –Ω–∏–∑–∫–∏–π —É—Ä–æ–≤–µ–Ω—å!', 
      color: '#FFF5F5', 
      text: '#C53030',
      animation: 'pulse 2s infinite'
    },
    warning: { icon: 'üîç', message: '–ù–∏–∑–∫–∏–π —É—Ä–æ–≤–µ–Ω—å –≤–æ–¥—ã', color: '#FFFAF0', text: '#B7791F' },
    normal: { icon: '‚úÖ', message: '–ù–æ—Ä–º–∞–ª—å–Ω—ã–π —É—Ä–æ–≤–µ–Ω—å', color: '#F0FFF4', text: '#2F855A' }
  };

  // –ê–Ω–∏–º–∞—Ü–∏—è –ø—É–∑—ã—Ä—å–∫–æ–≤
  useEffect(() => {
    if (!waterRef.current || waterLevel === null || waterLevel === 0) return;

    const container = waterRef.current;
    const createBubble = () => {
      if (status === 'danger') return; // –ù–µ –ø–æ–∫–∞–∑—ã–≤–∞—Ç—å –ø—É–∑—ã—Ä–∏ –ø—Ä–∏ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–º —É—Ä–æ–≤–Ω–µ
      
      const bubble = document.createElement('div');
      bubble.className = 'water-bubble';
      
      // –°–ª—É—á–∞–π–Ω—ã–π —Ä–∞–∑–º–µ—Ä –∏ –ø–æ–∑–∏—Ü–∏—è
      const size = Math.random() * 10 + 5;
      const left = Math.random() * 80 + 10;
      const delay = Math.random() * 3;
      
      bubble.style.cssText = `
        width: ${size}px;
        height: ${size}px;
        left: ${left}%;
        bottom: 0;
        animation-delay: ${delay}s;
        opacity: ${Math.random() * 0.5 + 0.3};
      `;
      
      container.appendChild(bubble);
      
      // –£–¥–∞–ª–µ–Ω–∏–µ –ø–æ—Å–ª–µ –∞–Ω–∏–º–∞—Ü–∏–∏
      setTimeout(() => {
        bubble.remove();
      }, 3000);
    };

    const bubbleInterval = setInterval(createBubble, 800);
    return () => clearInterval(bubbleInterval);
  }, [waterLevel, status]);

  return (
    <div className="water-card">
      <div className="water-header">
        <h2 className="water-title">
          <span className="icon">üíß</span> –£—Ä–æ–≤–µ–Ω—å –≤–æ–¥—ã
        </h2>
        <div className="system-icons">
          <span className="icon">üåø</span>
          <span className="icon">üêü</span>
        </div>
      </div>

      <div className="water-display">
        {waterLevel !== null ? (
          <>
            <div className="water-visualization" ref={waterRef}>
              <div 
                className="water-level"
                style={{
                  height: `${waterLevel}%`,
                  background: `linear-gradient(to bottom, ${colors.water}, ${colors.normal})`,
                  boxShadow: `inset 0 ${waterLevel > 10 ? -10 : 0}px 15px rgba(0,0,0,0.1)`
                }}
              />
              <div className="water-container">
                {waterLevel > 0 && (
                  <div className="water-surface" style={{ bottom: `${waterLevel}%` }} />
                )}
              </div>
            </div>
          </>
        ) : (
          <div className="no-data">
            <span className="icon">‚ùì</span> –ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö
          </div>
        )}
      </div>

      <div className="water-footer">
        <p 
          className="status-message" 
          style={{ 
            backgroundColor: statusConfig[status].color,
            color: statusConfig[status].text,
            animation: statusConfig[status].animation
          }}
        >
          {statusConfig[status].message}
        </p>
      </div>
    </div>
  );
};

export default Temperature ;
