import React from 'react';
import './Temperature.css';

const Temperature = ({ temperature }) => {
  const colors = {
    dark: '#3A5C40',        // Темно-хвойный (недостаток света)
    low: '#5D8C5A',         // Молодая листва
    ideal: '#7CB45D',       // Сочная зелень
    high: '#F0A830',        // Солнечный ореол
    danger: '#E05038',      // Осенний красный
    textDark: '#1E3B23',
    textLight: '#F5F7E6',
    moss: '#8AAE92',
    fern: '#6B8C42'
  };

  const getTempColor = temp => {
    if (temp === null) return colors.textDark;
    else if (temp < 14) return colors.dark;
    else if (temp < 26) return colors.ideal;
    else if (temp < 30) return colors.high;
    else return colors.danger;
  };

  return (
    <div className="temperature-card">
      <div className="temperature-header">
        <h2 className="temperature-title">
          <span className="icon">🌱</span> Температура
        </h2>
        <div className="ecosystem-icons">
          <span className="icon">💧</span>
          <span className="icon">🐟</span>
        </div>
      </div>

      <div className="temperature-display">
        <div className="temperature-value" style={{ color: getTempColor(temperature) }}>
          {temperature !== null ? temperature.toFixed(1) : '--'} <span className="temperature-unit">°C</span>
        </div>
        <div className="temperature-scale">
          <span style={{ color: colors.dark }}>10°</span>
          <div className="scale-bar">
            <div
              className="scale-progress"
              style={{
                width: temperature ? `${Math.min(100, ((temperature - 10) / 40) * 100)}%` : '0%',
                background: getTempColor(temperature)
              }}
            />
          </div>
          <span style={{ color: colors.danger }}>50°</span>
        </div>
      </div>

      <div className="temperature-footer">
        {temperature !== null && (
          <p className={`status-message ${getStatusClass(temperature)}`}>
            {temperature < 14 ? '❄️ Прохладно' :
             temperature < 26 ? '🌿 Норма' :
             temperature < 30 ? '⚠️ Тепло' : '🔥 Опасно'}
          </p>
        )}
      </div>
    </div>
  );

  function getStatusClass(temp) {
    if (temp < 14) return 'status-dark';
    else if (temp < 26) return 'status-ideal';
    else if (temp < 30) return 'status-high';
    else return 'status-danger';
  }
};

export default Temperature;
